import "./App.css";

function App() {
	const moviesObj = {};
	const [movies, setMovies] = useState("");
	function fetchMoviesHandler() {
		fetch("https://swapi.dev/api/films")
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				moviesObj["title"] = data.title;
				moviesObj["releaseDate"] = data.release_data;
				moviesObj["openingText"] = data.opening_crawl;

				setMovies(moviesObj);
				console.log(movies);
			});
	}

	return (
		<React.Fragment>
			<section>
				<button onClick={fetchMoviesHandler}></button>
			</section>
			<section>{/* <MoviesList /> */}</section>
		</React.Fragment>
	);
}

export default App;
